    #------I CREATED THIS FUNCTION TO DRAW TRIANGLE WITH ANGLES AND CORESPONDING SIDES
    #-------WITHOUT ANGLES I COUDNT MANAGED TO DRAW PROPER TRIANGLE.
    def calculate_angles(self, side1, side2, side3):
        angle_A = math.degrees(math.acos((side2 ** 2 + side3 ** 2 - side1 ** 2) / (2 * side2 * side3)))
        angle_B = math.degrees(math.acos((side1 ** 2 + side3 ** 2 - side2 ** 2) / (2 * side1 * side3)))
        angle_C = 180 - angle_A - angle_B

        return angle_A, angle_B, angle_C
        #------FUNCTION TO DRAW TRIANGLE
    def draw_triangle(self, side1, side2, side3):
        angles = self.calculate_angles(side1, side2, side3)
        sorted_angles = sorted(angles)
        sorted_sides = sorted([side1, side2, side3])


        turtle.setup(width=400, height=400)
        turtle.screensize(400, 400)
        turtle.speed(1)
        turtle.bgcolor("blue")


        turtle.penup()
        turtle.goto(-side1 / 2, -side2 / 2)
        turtle.pendown()
        turtle.fillcolor("red")
        turtle.begin_fill()



        turtle.forward(sorted_sides[0])
        turtle.left(180 - sorted_angles[1])

        turtle.forward(sorted_sides[2])
        turtle.left(180 - sorted_angles[0])

        turtle.forward(sorted_sides[1])
        turtle.left(180 - sorted_angles[2])

        turtle.end_fill()
        print(angles)
        print(sorted_sides, sorted_angles)

        turtle.done()
        #-----FUNCTION TO DRAW CIRCLE
    def draw_circle(self, radius):

        turtle.speed(1)
        turtle.setup(width=400, height=400)
        turtle.screensize(400, 400)
        turtle.bgcolor("green")
        turtle.fillcolor("yellow")
        turtle.begin_fill()


        turtle.circle(radius)
        turtle.end_fill()


        turtle.done()

    # -----FUNCTION TO DRAW RECTANGLE
    def draw_rectangle(self, length, width):

        turtle.speed(1)
        turtle.setup(width=400, height=400)
        turtle.screensize(400, 400)
        turtle.bgcolor("purple")
        turtle.fillcolor("pink")
        turtle.begin_fill()


        for _ in range(2):
            turtle.forward(length)
            turtle.left(90)
            turtle.forward(width)
            turtle.left(90)


        turtle.end_fill()
        turtle.done()

    def calculate_triangle(self):

        side1 = self.tri_first_side.value()
        side2 = self.tri_secend_side.value()
        side3 = self.tri_third_side.value()
        #----CHECKING IF I CAN FORM TRIANGLE WITH ENTERED VALUES
        if (side1 + side2 > side3) or (side1 + side3 > side2) or (side2 + side3 > side1):
            #-----CALCULATING MEDIANA
            ma = round(0.5 * math.sqrt(2 * side2 ** 2 + 2 * side3 ** 2 - side1 ** 2), 3)
            s = (side3 + side1 + side2) / 2
            area = round(math.sqrt(s * (s - side1) * (s - side2) * (s - side3)), 3)
            h = round(area / side1, 3)

            self.h_value.setText(str(h))
            self.area_value.setText(str(area))
            self.median_value.setText(str(ma))
            self.perimeter_value.setText(str(side1 + side2 + side3))
            self.draw_triangle(side1*10, side2*10, side3*10)




    def calculate_circle(self):
        cur_radius = self.radius_value.value()
        self.circle_area_value.setText(str(round(math.pi*cur_radius*cur_radius, 3)))
        self.circle_l_value.setText(str(round(math.pi*2*cur_radius, 3)))
        self.draw_circle(cur_radius*10)


    def calculate_rectangle(self):
        h = self.spinBox_9.value()
        w = self.rect_width.value()
        self.rect_perimeter.setText(str(h+h+w+w))
        self.rect_area.setText(str(h*w))
        self.draw_rectangle(w*10,h*10)

    def calculate_trapezoid(self):
        a = self.trapezia_big_value.value()
        b = self.trapezia_smal_value.value()
        c = self.trapecia_side_value.value()
        d = c
        self.trapezia_perimeter.setText(str(a+b+c+d))
        h = math.sqrt(c ** 2 - ((b - a) ** 2 + c ** 2 - d ** 2) / (2 * (b - a)) ** 2)
        area = 0.5 * (a + b) * h

        self.trapezia_area.setText(str(round(area,3)))
        self.trapezia_h.setText(str(round(h,3)))
        self.trapezia_middle_line.setText(str(round((a+b)/2,3)))


